import React, { useState } from 'react';
import { MessageSquare, Send, Clock, Calendar, Sparkles, Bot } from 'lucide-react';
import { format, addDays, startOfWeek, isAfter, isBefore, parseISO } from 'date-fns';

const AIAssistant = ({ availability, bookings }) => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      type: 'ai',
      content: "Hi! I'm your AI assistant. I can help you find the best times for appointments based on your availability. Try asking me something like 'When are you free this week?' or 'I need a meeting next Monday afternoon'."
    }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  // AI logic to analyze availability and suggest times
  const analyzeAvailability = (userInput) => {
    const input = userInput.toLowerCase();
    const suggestions = [];

    // Extract date/time preferences from user input
    const today = new Date();
    const nextWeek = addDays(today, 7);

    // Check for specific days mentioned
    const dayKeywords = {
      'monday': 1, 'mon': 1,
      'tuesday': 2, 'tue': 2,
      'wednesday': 3, 'wed': 3,
      'thursday': 4, 'thu': 4,
      'friday': 5, 'fri': 5,
      'saturday': 6, 'sat': 6,
      'sunday': 0, 'sun': 0
    };

    // Check for time preferences
    const timeKeywords = {
      'morning': { start: '09:00', end: '12:00' },
      'afternoon': { start: '12:00', end: '17:00' },
      'evening': { start: '17:00', end: '20:00' },
      'early': { start: '09:00', end: '11:00' },
      'late': { start: '16:00', end: '17:00' }
    };

    // Find available slots for the next 2 weeks
    for (let i = 0; i < 14; i++) {
      const checkDate = addDays(today, i);
      const dayName = format(checkDate, 'EEEE').toLowerCase();
      const dayAvailability = availability[dayName];

      if (dayAvailability && dayAvailability.available) {
        const slots = generateTimeSlotsForDay(checkDate, dayAvailability, bookings);

        if (slots.length > 0) {
          // Check if user mentioned specific days
          let shouldInclude = true;
          for (const [keyword, dayNum] of Object.entries(dayKeywords)) {
            if (input.includes(keyword) && checkDate.getDay() !== dayNum) {
              shouldInclude = false;
              break;
            }
          }

          if (shouldInclude) {
            suggestions.push({
              date: checkDate,
              dayName: format(checkDate, 'EEEE'),
              dateFormatted: format(checkDate, 'MMM d'),
              slots: slots.slice(0, 3) // Limit to 3 suggestions per day
            });
          }
        }
      }
    }

    return suggestions;
  };

  const generateTimeSlotsForDay = (date, dayAvailability, existingBookings) => {
    const slots = [];
    const startTime = new Date(date);
    const [startHour, startMinute] = dayAvailability.start.split(':');
    startTime.setHours(parseInt(startHour), parseInt(startMinute), 0);

    const endTime = new Date(date);
    const [endHour, endMinute] = dayAvailability.end.split(':');
    endTime.setHours(parseInt(endHour), parseInt(endMinute), 0);

    // Generate 30-minute slots
    const currentTime = new Date(startTime);
    while (currentTime < endTime) {
      const timeString = format(currentTime, 'HH:mm');
      const isBooked = existingBookings.some(booking => 
        format(new Date(booking.date), 'yyyy-MM-dd') === format(date, 'yyyy-MM-dd') &&
        booking.time === timeString
      );

      if (!isBooked) {
        slots.push(timeString);
      }

      currentTime.setMinutes(currentTime.getMinutes() + 30);
    }

    return slots;
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim() || isProcessing) return;

    const userMessage = {
      id: messages.length + 1,
      type: 'user',
      content: inputMessage
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsProcessing(true);

    // Simulate AI processing delay
    setTimeout(() => {
      const suggestions = analyzeAvailability(inputMessage);

      let aiResponse = '';
      if (suggestions.length > 0) {
        aiResponse = "Based on your availability, here are some great times for your appointment:\n\n";
        suggestions.forEach(suggestion => {
          aiResponse += `ðŸ“… **${suggestion.dayName}, ${suggestion.dateFormatted}**\n`;
          suggestion.slots.forEach(slot => {
            aiResponse += `   â° ${slot}\n`;
          });
          aiResponse += '\n';
        });
        aiResponse += "You can click on any of these times in the booking interface to schedule your appointment!";
      } else {
        aiResponse = "I couldn't find any available times that match your request. Try asking for a different time period or check the availability settings.";
      }

      const aiMessage = {
        id: messages.length + 2,
        type: 'ai',
        content: aiResponse
      };

      setMessages(prev => [...prev, aiMessage]);
      setIsProcessing(false);
    }, 1500);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="bg-white rounded-lg shadow">
      {/* Header */}
      <div className="border-b border-gray-200 p-4">
        <div className="flex items-center">
          <div className="flex items-center justify-center w-10 h-10 bg-primary-100 rounded-full mr-3">
            <Bot className="h-5 w-5 text-primary-600" />
          </div>
          <div>
            <h2 className="text-lg font-semibold text-gray-900">AI Assistant</h2>
            <p className="text-sm text-gray-500">Ask me about available times</p>
          </div>
        </div>
      </div>

      {/* Messages */}
      <div className="h-96 overflow-y-auto p-4 space-y-4">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                message.type === 'user'
                  ? 'bg-primary-600 text-white'
                  : 'bg-gray-100 text-gray-900'
              }`}
            >
              <div className="whitespace-pre-line">{message.content}</div>
            </div>
          </div>
        ))}

        {isProcessing && (
          <div className="flex justify-start">
            <div className="bg-gray-100 text-gray-900 px-4 py-2 rounded-lg">
              <div className="flex items-center">
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-primary-600 mr-2"></div>
                Thinking...
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Input */}
      <div className="border-t border-gray-200 p-4">
        <div className="flex space-x-2">
          <div className="flex-1">
            <textarea
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask me about available times... (e.g., 'When are you free this week?', 'I need a meeting next Monday afternoon')"
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 resize-none"
              rows={2}
              disabled={isProcessing}
            />
          </div>
          <button
            onClick={handleSendMessage}
            disabled={!inputMessage.trim() || isProcessing}
            className="bg-primary-600 text-white px-4 py-2 rounded-lg hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
          >
            <Send className="h-4 w-4" />
          </button>
        </div>
      </div>

      {/* Quick Suggestions */}
      <div className="border-t border-gray-200 p-4">
        <h3 className="text-sm font-medium text-gray-700 mb-2">Quick suggestions:</h3>
        <div className="flex flex-wrap gap-2">
          {[
            "When are you free this week?",
            "I need a meeting next Monday",
            "Any afternoon slots available?",
            "What's your availability next week?"
          ].map((suggestion, index) => (
            <button
              key={index}
              onClick={() => setInputMessage(suggestion)}
              className="text-xs bg-gray-100 hover:bg-gray-200 text-gray-700 px-3 py-1 rounded-full transition-colors"
            >
              {suggestion}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AIAssistant;
