import React, { useState } from 'react';
import { Calendar, Clock, Users, Settings, MessageSquare, CheckCircle } from 'lucide-react';
import AvailabilityManager from './components/AvailabilityManager';
import BookingInterface from './components/BookingInterface';
import AIAssistant from './components/AIAssistant';

function App() {
  const [currentView, setCurrentView] = useState('booking'); // 'booking', 'availability', 'ai'
  const [availability, setAvailability] = useState({
    monday: { start: '09:00', end: '17:00', available: true },
    tuesday: { start: '09:00', end: '17:00', available: true },
    wednesday: { start: '09:00', end: '17:00', available: true },
    thursday: { start: '09:00', end: '17:00', available: true },
    friday: { start: '09:00', end: '17:00', available: true },
    saturday: { start: '10:00', end: '15:00', available: false },
    sunday: { start: '10:00', end: '15:00', available: false },
  });

  const [bookings, setBookings] = useState([]);

  const addBooking = (booking) => {
    setBookings([...bookings, { ...booking, id: Date.now() }]);
  };

  // Handle time slot clicks from AI assistant
  const handleTimeSlotClick = (date, time) => {
    // Switch to booking view and pre-select the date and time
    setCurrentView('booking');

    // You could also store the selected date/time in state to pre-populate the booking form
    // For now, we'll just switch to the booking view
    console.log(`AI suggested booking: ${date} at ${time}`);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <Calendar className="h-8 w-8 text-primary-600" />
              <h1 className="ml-2 text-xl font-semibold text-gray-900">Smart Calendar</h1>
            </div>
            <nav className="flex space-x-8">
              <button
                onClick={() => setCurrentView('booking')}
                className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                  currentView === 'booking'
                    ? 'bg-primary-100 text-primary-700'
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                <Users className="h-4 w-4 mr-1" />
                Booking
              </button>
              <button
                onClick={() => setCurrentView('availability')}
                className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                  currentView === 'availability'
                    ? 'bg-primary-100 text-primary-700'
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                <Settings className="h-4 w-4 mr-1" />
                Availability
              </button>
              <button
                onClick={() => setCurrentView('ai')}
                className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                  currentView === 'ai'
                    ? 'bg-primary-100 text-primary-700'
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                <MessageSquare className="h-4 w-4 mr-1" />
                AI Assistant
              </button>
            </nav>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        {currentView === 'booking' && (
          <BookingInterface 
            availability={availability} 
            bookings={bookings}
            onBooking={addBooking}
          />
        )}
        {currentView === 'availability' && (
          <AvailabilityManager 
            availability={availability} 
            setAvailability={setAvailability}
          />
        )}
        {currentView === 'ai' && (
          <AIAssistant 
            availability={availability}
            bookings={bookings}
            onTimeSlotClick={handleTimeSlotClick}
          />
        )}
      </main>
    </div>
  );
}

export default App;
